def can_make_words(blocks, word):
    def swap(arr, i, j):
        arr[i], arr[j] = arr[j], arr[i]

    def can_make_words_recursive(blocks, word):
        if not word:
            return True
        c = word[0].upper()
        for i in range(len(blocks)):
            if blocks[i][0] == c or blocks[i][1] == c:
                swap(blocks, i, 0)
                if can_make_words_recursive(blocks[1:], word[1:]):
                    swap(blocks, 0, i)
                    return True
                swap(blocks, 0, i)
        return False

    return can_make_words_recursive(blocks, word)

def main():
    blocks = [
        "BO", "XK", "DQ", "CP", "NA", 
        "GT", "RE", "TG", "QD", "FS", 
        "JW", "HU", "VI", "AN", "OB", 
        "ER", "FS", "LY", "PC", "ZM"
    ]

    words = [
        "", "A", "BARK", "BOOK", "TREAT", "COMMON", "SQUAD", "Confuse"
    ]

    for word in words:
        print(f"{word}\t{can_make_words(blocks, word)}")

if __name__ == "__main__":
    main()
```

