import random

def is_balanced(s, l):
    c = 0
    while l > 0:
        l -= 1
        if s[l] == ']':
            c += 1
        elif s[l] == '[':
            if c == 0:
                break
            c -= 1
    return c == 0

def shuffle(s, h):
    for i in range(h):
        x = random.randint(0, h - 1)
        t = s[x]
        s[x] = s[h - i - 1]
        s[h - i - 1] = t

def gen_sequence(s, n):
    if n > 0:
        s[:n] = '[' * n
        s[n:2 * n] = ']' * n
        shuffle(s, 2 * n)
    s[2 * n] = '\0'

def do_sequence(n):
    s = [''] * 64
    o = "False"
    gen_sequence(s, n)
    if is_balanced(s, 2 * n):
        o = "True"
    print(f"'{''.join(s)}': {o}")

def main():
    n = 0
    while n < 9:
        do_sequence(n)
        n += 1

if __name__ == "__main__":
    main()
