from functools import reduce

class Sieve(object):
    """Sieve of Eratosthenes"""
    def __init__(self):
        self._primes = []
        self._comps = {}
        self._max = 2;

    def isprime(self, n):
        """check if number is prime"""
        if n >= self._max: self._genprimes(n)
        return n >= 2 and n in self._primes

    def _genprimes(self, max):
        while self._max <= max:
