import math

def sieve_of_eratosthenes(max_num):
    sieve = [True] * (max_num // 2)
    for num in range(3, int(math.sqrt(max_num)) + 1, 2):
        if sieve[num // 2]:
            sieve[num * num // 2::num] = [False] * ((max_num - num * num - 1) // (2 * num) + 1)
    return [2] + [2 * i + 1 for i in range(1, max_num // 2) if sieve[i]]

def is_truncatable_prime(num, prime_list):
    str_num = str(num)
    if '0' in str_num:
        return False
    # Check left truncation
    while str_num:
        if int(str_num) not in prime_list:
            return False
        str_num = str_num[1:]
    # Check right truncation
    while str_num != num:
        if int(str_num) not in prime_list:
            return False
        str_num = str_num[:-1]
    return True

MAX = 1000000
prime_list = sieve_of_eratosthenes(MAX)

left_trunc = -1
right_trunc = -1
for prime in range(MAX - 1, 2, -2):
    if prime in prime_list and is_truncatable_prime(prime, prime_list):
        if right_trunc == -1:
            right = prime
            while right > 0 and right % 2 != 0 and right in prime_list:
                right //= 10
            if right == 0:
                right_trunc = prime
        if left_trunc == -1:
            str_prime = str(prime)
            if '0' not in str_prime:
                while str_prime:
                    if int(str_prime) not in prime_list:
                        break
                    str_prime = str_prime[1:]
                if not str_prime:
                    left_trunc = prime
        if left_trunc != -1 and right_trunc != -1:
            break

print("Left  Truncatable :", left_trunc)
print("Right Truncatable :", right_trunc)
