import random
from typing import List, TypeVar

T = TypeVar('T')

class SOfN:
    def __init__(self, n: int):
        self.sample = []
        self.i = 0
        self.n = n

    def process(self, item: T) -> List[T]:
        self.i += 1
        if self.i <= self.n:
            self.sample.append(item)
        elif random.randint(0, self.i - 1) < self.n:
            self.sample[random.randint(0, self.n - 1)] = item
        return self.sample

if __name__ == "__main__":
