import math

def is_prime(n):
    if n % 2 == 0:
        return n == 2
    if n % 3 == 0:
        return n == 3
    d = 5
    while d * d <= n:
        if n % d == 0:
            return False
        d += 2
        if n % d == 0:
            return False
        d += 4
    return True

def main():
    q = 929
    if not is_prime(q):
        return
    r = q
    while r > 0:
        r <<= 1
    d = 2 * q + 1
    while True:
        i = 1
        p = r
        while p != 0:
            i = (i * i) % d
            if p < 0:
                i *= 2
            if i > d:
                i -= d
            p <<= 1
        if i != 1:
            d += 2 * q
        else:
            break
    print(f"2^{q}-1 = 0 (mod {d})")

if __name__ == "__main__":
    main()
```

