import itertools

def farey_sequence(n):
    seq = set()
    for d in range(1, n + 1):
        for n in range(d + 1):
            seq.add((n, d))
    return sorted(seq, key=lambda x: x[0] / x[1])

def main():
    for i in range(1, 6):
        seq = farey_sequence(i)
        print(f"F{i}: " + ", ".join(f"{a}/{b}" for a, b in seq))
    for i in range(100, 501, 100):
        seq = farey_sequence(i)
        print(f"F{i} has {len(seq)} terms.")

