class Fraction:
    def __init__(self, numerator, denominator):
        self.numerator = numerator
        self.denominator = denominator

    def __str__(self):
        return f"{self.numerator}/{self.denominator}"

class FareySequence:
    def __init__(self, n):
        self.n = n
        self.a, self.b = 0, 1
        self.c, self.d = 1, n

    def next(self):
        result = Fraction(self.a, self.b)
        k = (self.n + self.b) // self.d
        next_c = k * self.c - self.a
        next_d = k * self.d - self.b
        self.a, self.b = self.c, self.d
        self.c, self.d = next_c, next_d
        return result

    def has_next(self):
        return self.a <= self.n

def main():
    for n in range(1, 12):
        seq = FareySequence(n)
        print(f"{n}: {seq.next()}", end='')
        while seq.has_next():
            print(f" {seq.next()}", end='')
        print()

    for n in range(100, 1001, 100):
        count = 0
        seq = FareySequence(n)
        while seq.has_next():
            seq.next()
            count += 1
        print(f"{n}: {count}")

if __name__ == "__main__":
