def lcp(l):
    if len(l) == 0:
        return ""
    elif len(l) == 1:
        return l[0]

    min_str = l[0]
    max_str = l[0]

    for s in l[1:]:
        if s < min_str:
            min_str = s
        elif s > max_str:
            max_str = s

    for i in range(min(len(min_str), len(max_str))):
        if min_str[i] != max_str[i]:
            return min_str[:i]

    return min_str

