class Vector:
    def __init__(self, *args):
        if len(args) == 1 and isinstance(args[0], list) or isinstance(args[0], tuple):
            self.store = list(args[0])
        elif len(args) == 2:
            self.store = [args[0], args[1]]
        else:
            raise ValueError("Invalid arguments for Vector initialization")

    def __add__(self, other):
        return Vector([a + b for a, b in zip(self.store, other.store)])

    def __sub__(self, other):
        return Vector([a - b for a, b in zip(self.store, other.store)])

    def __mul__(self, scalar):
        return Vector([x * scalar for x in self.store])

    def __truediv__(self, scalar):
        return Vector([x / scalar for x in self.store])

    def __str__(self):
        return f"[{','.join(map(str, self.store))}]"

# Main function to test the Vector class
