def same_digits(n, b):
    f = n % b
    n //= b
    while n > 0:
        if n % b != f:
            return False
        n //= b
    return True

def is_brazilian(n):
    if n < 7:
        return False
    if n % 2 == 0:
        return True
    for b in range(2, n - 2):
        if same_digits(n, b):
            return True
    return False

def is_prime(n):
    if n < 2:
        return False
    if n % 2 == 0:
        return n == 2
    if n % 3 == 0:
        return n == 3
    d = 5
    while d * d <= n:
        if n % d == 0:
            return False
        d += 2
        if n % d == 0:
            return False
        d += 4
    return True

def main():
