import itertools

def print_list(ls):
    print("[", end="")
    iter_ls = iter(ls)
    if next(iter_ls, None) is not None:
        print(next(iter_ls), end="")
    for x in iter_ls:
        print(", ", end="")
        print(x, end="")
    print("]")

def cast_out(base, start, last):
    ran = [y for y in range(base - 1) if y % (base - 1) == (y * y) % (base - 1)]
    x = start // (base - 1)
    result = []
    while True:
        for n in ran:
            k = (base - 1) * x + n
            if k < start:
                continue
            if k > last:
                return result
            result.append(k)
        x += 1
    return result

def main():
    print_list(cast_out(16, 1, 255))
    print_list(cast_out(10, 1, 99))
    print_list(cast_out(17, 1, 288))

