    def FibonacciN(self, iStep, Count, First=0):
        T = [0] * self.nmax
        T[1] = First if First != 0 else 1
        T[2] = 1
        for i in range(3, Count + 1):
            Sigma = 0
            for j in range(i - 1, i - iStep - 1, -1):
                if j > 0:
                    Sigma += T[j]
            T[i] = Sigma
        c = ""
        for i in range(1, self.nmax):
            c += ", " + str(T[i])
        return c[2:]

