def is_prime(n):
    if n < 2:
        return False
    if n == 2:
        return True
    if n % 2 == 0:
        return False
    if n < 9:
        return True
    if n % 3 == 0:
        return False
    r = int(n**0.5)
    f = 5
    while f <= r:
        if n % f == 0:
            return False
        if n % (f + 2) == 0:
            return False
        f += 6
    return True

def primefactor_count(n):
    if n == 1:
        return 0
    if is_prime(n):
        return 1
    count = 0
    f = 2
    while True:
        if n % f == 0:
            count += 1
            n //= f
            if n == 1:
                return count
            if is_prime(n):
                f = n
        elif f >= 3:
            f += 2
        else:
            f = 3
        if f * f > n:
            break
    return count

def main():
    MAX = 120
    print(f"The attractive numbers up to and including {MAX} are:")
    i = 1
    count = 0
    while i <= MAX:
        n = primefactor_count(i)
        if is_prime(n):
            print(f"{i:4}", end='')
            count += 1
            if count % 20 == 0:
                print()
        i += 1
    print()

