        if i % 3 == 0:
            i += 2
            continue

        check_factor(i, n, factors)
        if n == 1:
            break
        i += 2

    return " x ".join(map(str, factors))

def check_factor(mult: int, n: int, factors: List[int]) -> None:
    while n % mult == 0:
        factors.append(mult)
        n //= mult

def main():
