def population_count(n: int) -> int:
    cnt = 0
    while n > 0:
        if n % 2 != 0:
            cnt += 1
        n >>= 1
    return cnt

def is_prime(x: int) -> bool:
    if x <= 2 or x % 2 == 0:
        return x == 2

    limit = int(x**0.5)
    for i in range(3, limit + 1, 2):
        if x % i == 0:
            return False

    return True

def pernicious(start: int, count: int, take: int) -> list[int]:
    return [n for n in range(start, start + count) if is_prime(population_count(n))][:take]

def main():
    for n in pernicious(0, float('inf'), 25):
        print(n, end=' ')
    print()

    for n in pernicious(888888877, 11, 11):
        print(n, end=' ')
    print()

if __name__ == "__main__":
