import math

def to_hash_set(source):
    return set(source)

def reverse_number(number):
    if number < 0:
        return -reverse_number(-number)
    if number < 10:
        return number

    rev = 0
    while number > 0:
        rev = rev * 10 + number % 10
        number //= 10
    return rev

def delimit(source, seperator=" "):
    return seperator.join(map(str, source))

def primes(bound):
    if bound < 2:
        return
    yield 2

    composite = [False] * ((bound - 1) // 2)
    limit = int((math.sqrt(bound) - 1) // 2)
    for i in range(limit):
        if composite[i]:
            continue
        prime = 2 * i + 3
        yield prime

        for j in range(int((prime * prime - 2) // 2), len(composite)):
            composite[j] = True
    for i in range(limit, len(composite)):
        if not composite[i]:
            yield 2 * i + 3

def find_emirp_primes(limit):
    ps = list(primes(limit))
    ps_set = set(ps)

    for p in ps:
        rev = reverse_number(p)
        if rev != p and rev in ps_set:
            yield p

def main():
    limit = 1_000_000
    print("First 20:")
    print(delimit(list(find_emirp_primes(limit))[:20]))
    print()

    print("Between 7700 and 8000:")
    print(delimit(list(find_emirp_primes(limit))[list(find_emirp_primes(limit)).index(7700):list(find_emirp_primes(limit)).index(8000)]))
    print()

    print("10000th:")
    print(list(find_emirp_primes(limit))[9999])

if __name__ == "__main__":
