from typing import Iterator, List, Tuple, Iterable, Callable

def loopy(seq: Iterable[T]) -> Iterator[T]:
    while True:
        for element in seq:
            yield element

def turn_wheels(*wheels: Tuple[str, str]) -> Iterator[str]:
    data = {wheel[0]: list(loopy(wheel[1])) for wheel in wheels}
    primary = data[wheels[0][0]]

    def turn(sequence: List[str]) -> str:
        while True:
            for char in sequence:
                if char.isdigit():
                    return char
                else:
                    sequence = data[char]

    while True:
        yield turn(primary)

def print_sequence(sequence: Iterable[str]):
    print(" ".join(sequence))

def main():
    print_sequence(list(turn_wheels("A", "123"))[:20])
    print_sequence(list(turn_wheels("A", "1B2"), ("B", "34"))[:20])
    print_sequence(list(turn_wheels("A", "1DD"), ("D", "678"))[:20])
    print_sequence(list(turn_wheels("A", "1BC"), ("B", "34"), ("C", "5B"))[:20])

if __name__ == "__main__":
    main()
