from typing import List

def main(args: List[str]):
    a: List[int] = [0]
    used: set[int] = {0}
    used1000: set[int] = used.copy()
    found_dup: bool = False
    n: int = 1
    while True:
        nv: int = a[-1] - n
        if nv < 1 or nv in used:
            nv += 2 * n
        already_used: bool = nv in used
        a.append(nv)
        if not already_used:
            used.add(nv)
            if nv > 0 and nv <= 1000:
                used1000.add(nv)
        if not found_dup:
            if len(a) == 15:
                print("The first 15 terms of the Recamn sequence are: ({})".format(", ".join(map(str, a))))
            if already_used:
                print("The first duplicated term is a({}) = {}".format(n, nv))
                found_dup = True
        if len(used1000) == 1001:
            print("Terms up to a({}) are needed to generate 0 to 1000".format(n))
            break
        n += 1

if __name__ == "__main__":
