def can_make_words(blocks, word):
    def swap(a, b):
        if a != b:
            tmp = a
            a = b
            b = tmp

    def can_make_words_recursive(b, word):
        c = word[0].upper()
        if not c:
            return True
        if not b[0]:
            return False

        for i in range(len(b)):
            if b[i][0] != c and b[i][1] != c:
                continue
            swap(b[i], b[0])
            result = can_make_words_recursive(b[1:], word[1:])
            swap(b[i], b[0])
            if result:
                return True
        return False

    return can_make_words_recursive(blocks, word)

def main():
