            long sum = 1;
            for (power = p; n % p == 0; power *= p, n /= p) {
                sum += power;
            }
            total *= sum;
        }
        // If n > 1 then it's prime
        if (n > 1) {
            total *= n + 1;
        }
        return total;
    }

