def sieve_of_eratosthenes(max_num):
    sieve = [True] * (max_num // 2)
    for i in range(3, int(max_num**0.5) + 1, 2):
        if sieve[i // 2]:
            sieve[i*i // 2::i] = [False] * ((max_num - i*i - 1) // (2*i) + 1)
    return sieve

def find_largest_truncatable_prime(max_num, sieve):
    right_trunc = -1
    left_trunc = -1
    for prime in range(max_num - 1, 2, -2):
